sp_widget.client_script:
function(spUtil, $scope) {
  /* widget controller */
  var c = this;

	spUtil.recordWatch($scope, "incident", "active=true");
}

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
	if(gs.nil(input))
		gs.addInfoMessage("nil");

	if(!gs.nil(input))
		gs.addInfoMessage("not nil");

		data.incident= [];
		var incGR = new GlideRecord('incident');
		//incGR.addQuery('caller_id', gs.getUser().getID());
		incGR.addQuery('active', true);
		incGR.query();
		while(incGR.next()){
			var incObj = {};
			$sp.getRecordDisplayValues(incObj, incGR, 'number,short_description,state,assignment_group,priority');
			data.incident.push(incObj); 
		}
	//}

})();
